async function moveTeamToOrg({
  team,
  org,
}: {
  team: {
    id: number;
  };
  org: {
    id: number;
  };
}) {
  await prisma.team.update({
    where: {
      id: team.id,
    },
    data: {
      parent: {
        connect: {
          id: org.id,
        },
      },
    },
  });
}
